mapper.update:
update tb_upload
set newfile = #{newfile}
where name = #{name}

update tb_upload
set thumbnail = #{thumbnail}
where name = #{name}

update tb_upload
set orgfile = #{orgfile}, newfile = #{newfile}, thumbnail = #{thumbnail}, name = #{name}, category = #{category}, version = #{version}, amount = #{amount}, newdate = #{newdate}  
where number = #{number}

update tb_upload
set newfile = #{newfile}
where number = #{number}

update tb_upload
set thumbnail = #{thumbnail}
where number = #{number}

mapper.select:
select * from tb_upload
1=1 category = #{category}
order by number
